import { Component, OnInit } from '@angular/core';
import { ModalService } from '../../../shared/services/modal.service';

interface Meeting {
  date: string;
  subject: string;
  teacher: string;
}

interface Filter {
  label: string;
  value: string;
}

@Component({
  selector: 'app-reuniones',
  templateUrl: './reuniones.component.html',
  styleUrls: ['./reuniones.component.scss']
})
export class ReunionesComponent implements OnInit {

  meetings: Meeting[] = [
    { date: '5 de Diciembre 11:00 - 11:55', subject: 'Proyecto Intermodular', teacher: 'Willman Acosta Lugo' },
    { date: '08 de Diciembre 12:00 - 12:55', subject: 'Despliegue en aplicaciones web', teacher: 'Carlos Basulto Pardo' },
    { date: '10 de diciembre 08:30 - 09:25', subject: 'Optativa', teacher: 'Willman Acosta Lugo' },
  ];

  filteredMeetings: Meeting[] = [];

  filters: Filter[] = [
    { label: 'Hoy', value: 'hoy' },
    { label: 'Esta semana', value: 'semana' },
    { label: 'Este mes', value: 'mes' }
  ];

  searchTerm: string = '';
  activeFilter: string | null = null;

  constructor(private modalService: ModalService) { }

  ngOnInit(): void {
    this.filteredMeetings = [...this.meetings];
  }

  toggleFilter(filterValue: string): void {
    if (this.activeFilter === filterValue) {
      this.activeFilter = null;
    } else {
      this.activeFilter = filterValue;
    }
  }

  applyFilters(): void {
    let filtered = [...this.meetings];

    // Filtro de búsqueda
    if (this.searchTerm) {
      const term = this.searchTerm.toLowerCase();
      filtered = filtered.filter(m =>
        m.date.toLowerCase().includes(term) ||
        m.subject.toLowerCase().includes(term) ||
        m.teacher.toLowerCase().includes(term)
      );
    }

    // Aquí podrías añadir la lógica real de filtros por fecha
    // Por ahora solo registramos cuál se aplicó
    console.log(`Filtro aplicado: ${this.activeFilter}`);

    this.filteredMeetings = filtered;
  }

  cancelMeeting(meeting: Meeting): void {
    this.modalService.confirm(
      'Cancelar Reunión',
      `¿Estás seguro de que quieres cancelar la reunión de ${meeting.subject} el ${meeting.date}?`,
      'Sí, Cancelar',
      'No, mantener'
    ).then(confirmed => {
      if (confirmed) {
        this.filteredMeetings = this.filteredMeetings.filter(m => m !== meeting);
        this.meetings = this.meetings.filter(m => m !== meeting);
      }
    });
  }
}
<main class="main-content">
  <div class="container">

    <!-- Buscador -->
    <div class="search-container">
      <div class="search-box">
        <svg class="search-icon" width="20" height="20" viewBox="0 0 20 20" fill="none">
          <circle cx="8" cy="8" r="6" stroke="#666" stroke-width="2"/>
          <path d="M12 12L18 18" stroke="#666" stroke-width="2" stroke-linecap="round"/>
        </svg>
        <input type="text" placeholder="Buscar" class="search-input" [(ngModel)]="searchTerm">
      </div>
    </div>

    <!-- Filtros -->
    <div class="filters-bar">
      <span class="filter-label">Filtros:</span>
      <button
        class="filter-btn"
        *ngFor="let filter of filters"
        [class.active]="activeFilter === filter.value"
        (click)="toggleFilter(filter.value)"
      >
        {{ filter.label }}
      </button>
      <button class="apply-filters-btn" (click)="applyFilters()">Aplicar filtros</button>
    </div>

    <h2 class="section-title">Próximas reuniones</h2>

    <!-- Lista de reuniones -->
    <div class="meetings-list">
      <div
        class="meeting-card"
        *ngFor="let meeting of filteredMeetings"
      >
        <div class="meeting-info">
          <div class="meeting-date">{{ meeting.date }}</div>
          <div class="meeting-subject">{{ meeting.subject }}</div>
        </div>
        <div class="meeting-teacher">{{ meeting.teacher }}</div>
        <button class="cancel-btn" (click)="cancelMeeting(meeting)">Cancelar reunión</button>
      </div>
    </div>
  </div>
</main>
